mapper.insert:

	SELECT IF( IFNULL(MAX(IDX), 0) = 0, 1, MAX(IDX)+1) FROM APP_INSTAR_M WHERE DEL_YN = 'N'

INSERT INTO 
	APP_INSTAR_M
(	IDX
,	IN_USER_ID
,	TEXT
,	IMG
)
VALUES
(
	#{IDX}
,	#{in_user_id}
,	#{text}
,	#{image}
);

INSERT INTO APP_INSTAR_D
(
	IDX
,	COUNT
,	USERLIKED
,	IN_USER_ID
)
VALUES
(
	#{idx}
,	1
,	'true'
,	#{in_user_id}
);

mapper.delete:
DELETE FROM	APP_INSTAR_D
WHERE	IDX 		= #{idx}
AND		IN_USER_ID	= #{in_user_id};
