mapper.select:
SELECT IDX , TITLE , CONTENT , PASSWD , IN_USER_ID , EMAIL , IN_DT , READCOUNT FROM BL_BD WHERE DEL_YN = 'N'
AND TITLE = #{searchText}
AND CONTENT = #{searchText}
AND IN_USER_ID = #{searchText}
AND TITLE = #{searchText} AND CONTENT = #{searchText}
ORDER BY IDX DESC

mapper.update:

	UPDATE	BL_BD 
	   SET
	   		TITLE 		= #{TITLE}
	   	,	CONTENT 	= #{CONTENT}
	   	,	IN_USER_ID 	= #{WRITER}
	   	,	UP_DT		= now()
	WHERE
		IDX = #{IDX}

mapper.insert:
 	
