mapper.insert:

	SELECT IF( IFNULL(MAX(IDX), 0) = 0, 1, MAX(IDX)+1) FROM TB_BLOG_M WHERE DEL_YN = 'N'

INSERT INTO TB_BLOG_M
(
	IDX
,	TITLE
,	SUBJECT
,	CONTENT
,	IN_USER_ID
)
VALUES
(
	#{IDX}
,	#{title}
,	#{subject}
,	#{content}
,	#{s_userId}
);

mapper.update:
UPDATE	TB_BLOG_M
SET
	SUBJECT	= #{subject}
,	CONTENT = #{content}
WHERE
	IDX	= #{idx};

mapper.insert:

SELECT IFNULL(MAX(REF) + 1, 1) AS REF FROM TB_BLOG_RE_M WHERE DEL_YN = 'N'

INSERT INTO TB_BLOG_RE_M
(
	IDX
,	REF
,	CONTENT
,	IN_DT
,	IN_USER_ID
)
VALUES
(
	#{idx}
,	#{REF}
,	#{content}
,	now()
,	#{writer}
)

mapper.update:
UPDATE TB_BLOG_RE_M
SET
	DEL_YN = 'Y'
WHERE
	IDX		= #{idx}
AND REF		= #{ref}
AND RE_STEP = #{re_step}

UPDATE TB_BLOG_RE_M
SET
	DEL_YN = 'Y'
WHERE
	IDX		= #{idx}
AND REF		= #{ref}

mapper.insert:

SELECT MAX(RE_STEP)+1 FROM TB_BLOG_RE_M WHERE REF = #{ref} AND DEL_YN = 'N'

INSERT INTO TB_BLOG_RE_M
(
	IDX
,	CONTENT
,	REF
,	RE_LEVEL
,	RE_STEP
,	IN_DT
,	IN_USER_ID
)
VALUES
(
	#{idx}
,	#{content}
,	#{ref}
,	'2'
,	#{RE_STEP}
,	now()
,	#{writer}
)
