SELECT IFNULL(MAX(REF) + 1, 1) AS REF FROM TB_BLOG_RE_M

INSERT INTO TB_BLOG_RE_M
(
	IDX
,	REF
,	CONTENT
,	IN_DT
,	IN_USER_ID
)
VALUES
(
	#{idx}
,	#{REF}
,	#{content}
,	now()
,	#{s_userId}
)

mapper.update:
UPDATE TB_BLOG_RE_M
SET
	CONTENT = #{content}
WHERE
	IDX		= #{idx}
AND REF		= #{ref}
AND RE_STEP = #{reStep}

UPDATE TB_BLOG_RE_M
SET
	DEL_YN = 'Y'
WHERE
	IDX		= #{idx}
AND REF		= #{ref}
AND RE_STEP = #{reStep}

UPDATE TB_BLOG_RE_M
SET
	DEL_YN = 'Y'
WHERE
	IDX		= #{idx}
AND REF		= #{ref}

mapper.insert:

SELECT MAX(RE_STEP)+1 FROM TB_BLOG_RE_M WHERE REF = #{ref} AND DEL_YN = 'N'

INSERT INTO TB_BLOG_RE_M
