mapper.select:
SELECT /* [CodeService.query][getCodeGroupList][코드그룹 목록조회 조회][Kim Jin Ho][2016-09-13] */ CODE_GROUP_SEQ , CODE_GROUP_CD , CODE_GROUP_NM , CODE_GROUP_DESC , CODE_GROUP_TYPE , SYS_YN , USE_YN , IN_USER_ID , TO_CHAR(IN_DT,'YYYY-MM-DD') IN_DT FROM TB_PARAGON_COMM_CODE_GROUP WHERE COMPANY_CD = #{s_companyCd}
AND CODE_GROUP_NM LIKE '%${codeGroupNm}%'
AND CODE_GROUP_TYPE = #{codeGroupType}
AND USE_YN = #{useYn}
ORDER BY CODE_GROUP_CD DESC

mapper.update:
UPDATE TB_PARAGON_COMM_CODE_GROUP /* [CodeService.query][updateCodeGroup][공통코드그룹 정보 수정][Kim Jin Ho][2016-09-13] */
   SET CODE_GROUP_NM = #{codeGroupNm}
     , CODE_GROUP_DESC = #{codeGroupDesc}
     , CODE_GROUP_TYPE = #{codeGroupType}
     , SYS_YN = #{sysYn}
     , USE_YN = #{useYn}
     , UP_USER_ID = #{s_userId}
     , UP_DT = SYSDATE
 WHERE CODE_GROUP_SEQ = #{codeGroupSeq}
   AND COMPANY_CD = #{s_companyCd}

mapper.insert:
INSERT /* [CodeService.query][insertCodeGroup][공통코드그룹 정보 등록][Kim Jin Ho][2016-09-13] */
  INTO TB_PARAGON_COMM_CODE_GROUP (
  		  COMPANY_CD
		, CODE_GROUP_SEQ
		, CODE_GROUP_CD
		, CODE_GROUP_NM
		, CODE_GROUP_DESC
		, CODE_GROUP_TYPE
		, SYS_YN
		, USE_YN
		, IN_USER_ID
		, UP_USER_ID
  )
VALUES(	  #{s_companyCd}
		, SQ_COMM_CODE_GROUP01.NEXTVAL
		, #{codeGroupCd}
		, #{codeGroupNm}
		, #{codeGroupDesc}
		, #{codeGroupType}
		, #{sysYn}
		, #{useYn}
		, #{s_userId}
		, #{s_userId}
)

mapper.delete:
DELETE /* [CodeService.query][deleteCodeGroup][공통코드그룹 정보 삭제][Kim Jin Ho][2016-09-13] */
  FROM TB_PARAGON_COMM_CODE_GROUP
 WHERE CODE_GROUP_CD = #{codeGroupCd}
   AND COMPANY_CD = #{s_companyCd}

mapper.select:
SELECT /* [CodeService.query][getCodeList][프로그램 목록조회 조회][Kim Jin Ho][2016-09-13] */ CODE_SEQ , CODE_CD , CODE_GROUP_CD , CODE_NM , CODE_ORDER , CODE_DESC , CODE_OTHER1 , CODE_OTHER2 , CODE_OTHER3 , CODE_OTHER4 , CODE_OTHER5 , USE_YN , IN_USER_ID , TO_CHAR(IN_DT,'YYYY-MM-DD') IN_DT FROM TB_PARAGON_COMM_CODE WHERE DEL_YN = 'N' AND COMPANY_CD = #{s_companyCd}
AND CODE_GROUP_CD = #{codeGroupCd}
ORDER BY CODE_GROUP_CD ASC , CODE_ORDER ASC

mapper.update:
UPDATE TB_PARAGON_COMM_CODE /* [CodeService.query][updateProgram][공통코드 정보 수정][Kim Jin Ho][2016-09-13] */
   SET CODE_CD 		= #{codeCd}
     , CODE_NM 		= #{codeNm}
     , CODE_DESC 	= #{codeDesc}
     , CODE_ORDER 	= #{codeOrder}
     , CODE_OTHER1 	= #{codeOther1}
     , CODE_OTHER2 	= #{codeOther2}
     , CODE_OTHER3 	= #{codeOther3}
     , CODE_OTHER4 	= #{codeOther4}
     , CODE_OTHER5 	= #{codeOther5}
     , USE_YN 		= #{useYn}
     , IN_USER_ID 	= #{s_userId}
 WHERE CODE_SEQ = #{codeSeq}
   AND COMPANY_CD = #{s_companyCd}

